deb.top('../mod_apache2')
deb.control('debian/control')
deb.copy_tmpl('debian/postinst','/DEBIAN/postinst')
deb.copy_tmpl('debian/postrm','/DEBIAN/postrm')
deb.perm('/DEBIAN/postinst','755')
deb.perm('/DEBIAN/postrm','755')
deb.mkdir(deb.conf.apache_modules)
deb.mkdir(deb.conf.apache_mods_available)
deb.copy('mod_luasp.so',deb.conf.apache_modules)
deb.copy('debian/luasp.conf',deb.conf.apache_mods_available)
deb.copy_tmpl('debian/luasp.load',deb.conf.apache_mods_available..'/luasp.load')
deb.build()
